class Solution {
    func checkInclusion(_ s1: String, _ s2: String) -> Bool {
        if s1.count > s2.count {
            return false
        }
        // s2 [ idx: [ c: 1, a: 2, b: 3, ] ]
        // s1 [ c: 1, a: 2, b: 3, ]
        // O(n^2)
        let c1 = Array(s1)
        let c2 = Array(s2)

        var m1 = [Character: Int]()
        var m2 = [Int: [Character: Int]]()
        for i in 0..<s1.count {
            let c = c1[i]
            if let cnt = m1[c] {
                m1[c] = cnt + 1
            } else {
                m1[c] = 1
            }
        }

        let length = c1.count
        for i in (0..<c2.count).reversed() {
            let c = c2[i]
            if i < c2.count-1 {
                var preMap = m2[i+1]!
                // remove before
                if i + length < c2.count {
                    let removeChar = c2[i+length]
                    preMap[removeChar] = preMap[removeChar]! - 1
                }
                // add new
                if let cnt = preMap[c] {
                    preMap[c] = cnt + 1
                } else {
                    preMap[c] = 1
                }
                m2[i] = preMap
            } else {
                m2[i] = [c : 1]
            }
        }

        // print("m1 = \(m1)")
        // print("m2 = \(m2)")

        for i in 0...c2.count-c1.count {
            let mm2 = m2[i]!
            var contains = true
            // match
            for k in m1.keys {
                if let cnt = mm2[k] {
                    if cnt != m1[k] {
                        contains = false
                        break
                    }
                } else {
                    contains = false
                    break
                }
            }
            if contains {
                return true
            }
        }
        return false
    }
}


class Solution2 {
    func checkInclusion(_ s1: String, _ s2: String) -> Bool {
        if s1.count > s2.count {
            return false
        }
        let c1 = Array(s1)
        let c2 = Array(s2)
        for i in 0...c2.count-c1.count {
            var c1t = c1
            for j in 0..<c1.count {
                if let index = c1t.firstIndex(of: c2[i+j]) {
                   c1t.remove(at: index)
                } else {
                    break
                }
            }
            if c1t.count == 0 {
                return true
            }
        }
        return false
    }
}

